signature-rule.cpp<pre><code> 1class Animal {
 2public:
 3    virtual void makeSound(int volume) {
 4        cout &lt;&lt; "Animal sound at volume " &lt;&lt; volume &lt;&lt; endl;
 5    }
 6};
 7
 8class Dog : public Animal {
 9public:
10    // ✅ Correct: Same signature → valid overriding
11    void makeSound(int volume) override {
12        cout &lt;&lt; "Dog barks at volume " &lt;&lt; volume &lt;&lt; endl;
13    }
14
15    // ❌ Incorrect: Different signature → overloads, not overrides
16    void makeSound(string type) {
17        cout &lt;&lt; "Dog makes " &lt;&lt; type &lt;&lt; " sound" &lt;&lt; endl;
18    }
19};
</code></pre>

