singleton-design.cpp<pre><code> 1#include &lt;iostream&gt;
 2#include &lt;mutex&gt;
 3
 4class Singleton {
 5private:
 6    static Singleton* instance;
 7    static std::mutex mtx;
 8
 9    // Private constructor
10    Singleton() {
11        std::cout &lt;&lt; "Singleton Instance Created\n";
12    }
13
14public:
15    // Delete copy/move operations
16    Singleton(const Singleton&amp;) = delete;
17    Singleton&amp; operator=(const Singleton&amp;) = delete;
18
19    static Singleton* getInstance() {
20        std::lock_guard&lt;std::mutex&gt; lock(mtx);
21        if (instance == nullptr) {
22            instance = new Singleton();
23        }
24        return instance;
25    }
26
27    void showMessage() {
28        std::cout &lt;&lt; "Hello from Singleton instance!" &lt;&lt; std::endl;
29    }
30};
31
32// Initialize static members
33Singleton* Singleton::instance = nullptr;
34std::mutex Singleton::mtx;
35
36int main() {
37    Singleton* s1 = Singleton::getInstance();
38    Singleton* s2 = Singleton::getInstance();
39
40    s1-&gt;showMessage();
41
42    if (s1 == s2)
43        std::cout &lt;&lt; "Both instances are the same (Singleton works!)" &lt;&lt; std::endl;
44
