  7
  8using namespace std;
  9
 10// ---------- ENUMS ----------
 11enum class OrderStatus {
 12    PLACED,
 13    ACCEPTED,
 14    PREPARING,
 15    OUT_FOR_DELIVERY,
 16    DELIVERED
 17};
 18
 19// ---------- ENTITY CLASSES ----------
 20
 21class FoodItem {
 22    string name;
 23    double price;
 24public:
 25    FoodItem(string n, double p) : name(n), price(p) {}
 26    string getName() const { return name; }
 27    double getPrice() const { return price; }
 28};
 29
 30class Restaurant {
 31    string name;
 32    vector&lt;FoodItem&gt; menu;
 33public:
 34    Restaurant(string n, vector&lt;FoodItem&gt; m) : name(n), menu(m) {}
 35
 36    string getName() const { return name; }
 37    vector&lt;FoodItem&gt; getMenu() const { return menu; }
 38
 39    void displayMenu() const {
 40        cout &lt;&lt; "\n🍴 Menu - " &lt;&lt; name &lt;&lt; ":\n";
 41        for (size_t i = 0; i &lt; menu.size(); ++i)
 42            cout &lt;&lt; i + 1 &lt;&lt; ". " &lt;&lt; menu[i].getName() &lt;&lt; " - Rs. " &lt;&lt; menu[i].getPrice() &lt;&lt; endl;
 43    }
