 45    void prepareOrder() {
 46        cout &lt;&lt; "\n👨‍🍳 " &lt;&lt; name &lt;&lt; " is preparing your food...\n";
 47        this_thread::sleep_for(chrono::seconds(2));
 48        cout &lt;&lt; "✅ Food prepared successfully!\n";
 49    }
 50};
 51
 52class Cart {
 53    vector&lt;FoodItem&gt; items;
 54public:
