 65    }
 66
 67    void showCart() const {
 68        cout &lt;&lt; "\n🛍️ Cart Items:\n";
 69        for (const auto&amp; item : items)
 70            cout &lt;&lt; "- " &lt;&lt; item.getName() &lt;&lt; " : Rs. " &lt;&lt; item.getPrice() &lt;&lt; endl;
 71        cout &lt;&lt; "Total: Rs. " &lt;&lt; getTotal() &lt;&lt; "\n";
 72    }
 73
 74    vector&lt;FoodItem&gt; getItems() const { return items; }
 75};
 76
 77class Payment {
 78public:
 79    static bool process(double amount) {
 80        cout &lt;&lt; "\n💳 Processing payment of Rs. " &lt;&lt; amount &lt;&lt; "...\n";
 81        this_thread::sleep_for(chrono::seconds(1));
 82        cout &lt;&lt; "✅ Payment successful!\n";
 83        return true;
 84    }
 85};
 86
 87class Order {
 88    string id;
 89    OrderStatus status;
 90    vector&lt;FoodItem&gt; items;
 91public:
 92    Order(vector&lt;FoodItem&gt; i) : items(i), status(OrderStatus::PLACED) {
 93        id = "ORD" + to_string(rand() % 10000 + 1000);
 94    }
 95
 96    string getId() const { return id; }
 97    void updateStatus(OrderStatus s) { status = s; }
 98
 99    void trackOrder() const {
100        cout &lt;&lt; "🚚 Order Status: ";
101        switch (status) {
102            case OrderStatus::PLACED: cout &lt;&lt; "Placed\n"; break;
103            case OrderStatus::ACCEPTED: cout &lt;&lt; "Accepted\n"; break;
104            case OrderStatus::PREPARING: cout &lt;&lt; "Preparing\n"; break;
105            case OrderStatus::OUT_FOR_DELIVERY: cout &lt;&lt; "Out for delivery\n"; break;
106            case OrderStatus::DELIVERED: cout &lt;&lt; "Delivered\n"; break;
107        }
108    }
109};
110
111class DeliveryPerson {
112    string name;
113public:
114    DeliveryPerson(string n) : name(n) {}
115
116    void deliverOrder(Order&amp; order) {
117        cout &lt;&lt; "\n🚴 " &lt;&lt; name &lt;&lt; " picked up the order.\n";
118        order.updateStatus(OrderStatus::OUT_FOR_DELIVERY);
119        order.trackOrder();
120        this_thread::sleep_for(chrono::seconds(2));
121        order.updateStatus(OrderStatus::DELIVERED);
122        cout &lt;&lt; "✅ " &lt;&lt; name &lt;&lt; " delivered your order successfully!\n";
123    }
124};
125
126class User {
127    string name;
128    Cart cart;
129public:
130    User(string n) : name(n) {}
131
132    void searchRestaurant(const vector&lt;Restaurant&gt;&amp; restaurants) {
133        cout &lt;&lt; "\n🔍 Searching restaurants near you...\n";
134        for (size_t i = 0; i &lt; restaurants.size(); ++i)
135            cout &lt;&lt; i + 1 &lt;&lt; ". " &lt;&lt; restaurants[i].getName() &lt;&lt; endl;
136    }
137
138    void addToCart(const FoodItem&amp; item) { cart.addItem(item); }
139
140    void checkout(Restaurant&amp; restaurant) {
141        cart.showCart();
142        double total = cart.getTotal();
143        if (Payment::process(total)) {
144            Order order(cart.getItems());
145            cout &lt;&lt; "\n🧾 Order ID: " &lt;&lt; order.getId() &lt;&lt; " placed successfully!\n";
146            order.updateStatus(OrderStatus::ACCEPTED);
147            restaurant.prepareOrder();
148            order.updateStatus(OrderStatus::PREPARING);
149            order.trackOrder();
150
151            DeliveryPerson delivery("Rahul");
152            delivery.deliverOrder(order);
153        }
154    }
155};
156
157// ---------- MAIN FUNCTION ----------
158int main() {
159    srand(time(nullptr));
160
161    // Sample menu
162    Restaurant dominoz("Dominoz", {
163        FoodItem("Veg Pizza", 250),
