167
168    vector&lt;Restaurant&gt; restaurants = { dominoz };
169
170    User raj("Raj Gupta");
171    raj.searchRestaurant(restaurants);
172
173    // User views menu and adds to cart
174    dominoz.displayMenu();
175    raj.addToCart(dominoz.getMenu()[0]);
176    raj.addToCart(dominoz.getMenu()[2]);
177
178    // Checkout
179    raj.checkout(dominoz);
180
181    return 0;
182}
</code></pre>

observer-design.cpp<pre><code> 1#include &lt;iostream&gt;
 2#include &lt;vector&gt;
 3#include &lt;algorithm&gt;
 4using namespace std;
 5
 6// ----- Observer Interface -----
 7class Observer {
 8public:
 9    virtual void update(float temp) = 0;
10    virtual ~Observer() = default;
11};
12
13// ----- Subject Interface -----
14class Subject {
15public:
16    virtual void attach(Observer* obs) = 0;
17    virtual void detach(Observer* obs) = 0;
18    virtual void notify() = 0;
19    virtual ~Subject() = default;
20};
21
22// ----- Concrete Subject -----
23class WeatherStation : public Subject {
24    float temperature;
25    vector&lt;Observer*&gt; observers;
26
27public:
28    void attach(Observer* obs) override {
29        observers.push_back(obs);
30    }
31
32    void detach(Observer* obs) override {
33        observers.erase(remove(observers.begin(), observers.end(), obs), observers.end());
34    }
35
36    void setTemperature(float temp) {
37        temperature = temp;
38        cout &lt;&lt; "\n🌡️ Temperature updated to: " &lt;&lt; temp &lt;&lt; "°C\n";
39        notify();
40    }
41
42    void notify() override {
43        for (auto* obs : observers)
44            obs-&gt;update(temperature);
45    }
46};
47
48// ----- Concrete Observers -----
49class PhoneDisplay : public Observer {
50public:
51    void update(float temp) override {
52        cout &lt;&lt; "📱 Phone Display -&gt; Current Temp: " &lt;&lt; temp &lt;&lt; "°C\n";
53    }
54};
55
56class WindowDisplay : public Observer {
57public:
58    void update(float temp) override {
59        cout &lt;&lt; "🖥️ Window Display -&gt; Current Temp: " &lt;&lt; temp &lt;&lt; "°C\n";
60    }
61};
62
63// ----- Main -----
64int main() {
65    WeatherStation station;
66    PhoneDisplay phone;
67    WindowDisplay window;
68
69    // Observers subscribe
70    station.attach(&amp;phone);
71    station.attach(&amp;window);
72
73    // Change state
74    station.setTemperature(28.5);
75    station.setTemperature(31.2);
76
77    // Detach one observer
78    station.detach(&amp;phone);
79    station.setTemperature(26.8);
80
81    return 0;
82}
</code></pre>

decorator-design.cpp<pre><code> 1#include &lt;iostream&gt;
 2#include &lt;memory&gt;
 3using namespace std;
 4
 5// ----- Component Interface -----
 6class Coffee {
 7public:
 8    virtual double getCost() const = 0;
 9    virtual string getDescription() const = 0;
10    virtual ~Coffee() = default;
11};
12
13// ----- Concrete Component -----
14class SimpleCoffee : public Coffee {
15public:
16    double getCost() const override { return 50.0; }
17    string getDescription() const override { return "Simple Coffee"; }
18};
19
20// ----- Abstract Decorator -----
21class CoffeeDecorator : public Coffee {
22protected:
23    shared_ptr&lt;Coffee&gt; coffee;
24public:
25    CoffeeDecorator(shared_ptr&lt;Coffee&gt; c) : coffee(move(c)) {}
26};
27
28// ----- Concrete Decorators -----
29class MilkDecorator : public CoffeeDecorator {
30public:
31    MilkDecorator(shared_ptr&lt;Coffee&gt; c) : CoffeeDecorator(move(c)) {}
32    double getCost() const override { return coffee-&gt;getCost() + 20.0; }
33    string getDescription() const override { return coffee-&gt;getDescription() + ", Milk"; }
34};
35
36class SugarDecorator : public CoffeeDecorator {
37public:
38    SugarDecorator(shared_ptr&lt;Coffee&gt; c) : CoffeeDecorator(move(c)) {}
39    double getCost() const override { return coffee-&gt;getCost() + 10.0; }
40    string getDescription() const override { return coffee-&gt;getDescription() + ", Sugar"; }
41};
42
43class CaramelDecorator : public CoffeeDecorator {
44public:
45    CaramelDecorator(shared_ptr&lt;Coffee&gt; c) : CoffeeDecorator(move(c)) {}
46    double getCost() const override { return coffee-&gt;getCost() + 30.0; }
47    string getDescription() const override { return coffee-&gt;getDescription() + ", Caramel"; }
48};
49
50// ----- Client Code -----
51int main() {
52    shared_ptr&lt;Coffee&gt; coffee = make_shared&lt;SimpleCoffee&gt;();
53    cout &lt;&lt; coffee-&gt;getDescription() &lt;&lt; " → ₹" &lt;&lt; coffee-&gt;getCost() &lt;&lt; endl;
54
55    coffee = make_shared&lt;MilkDecorator&gt;(coffee);
56    coffee = make_shared&lt;SugarDecorator&gt;(coffee);
57    coffee = make_shared&lt;CaramelDecorator&gt;(coffee);
58
59    cout &lt;&lt; coffee-&gt;getDescription() &lt;&lt; " → ₹" &lt;&lt; coffee-&gt;getCost() &lt;&lt; endl;
60}
</code></pre>

notification-system.cpp<pre><code> 1#include &lt;iostream&gt;
 2#include &lt;vector&gt;
 3#include &lt;memory&gt;
 4#include &lt;string&gt;
 5using namespace std;
 6
 7// ----- Notification Interface -----
 8class Notification {
 9public:
10    virtual void send(const string&amp; message) = 0;
11    virtual ~Notification() = default;
12};
13
14// ----- Concrete Notification Types -----
15class EmailNotification : public Notification {
16public:
17    void send(const string&amp; message) override {
18        cout &lt;&lt; "📧 Sending Email: " &lt;&lt; message &lt;&lt; endl;
19    }
20};
21
22class SMSNotification : public Notification {
23public:
24    void send(const string&amp; message) override {
25        cout &lt;&lt; "📱 Sending SMS: " &lt;&lt; message &lt;&lt; endl;
26    }
27};
28
29class PopupNotification : public Notification {
30public:
31    void send(const string&amp; message) override {
32        cout &lt;&lt; "💬 Showing Popup: " &lt;&lt; message &lt;&lt; endl;
33    }
34};
35
36// ----- Factory Pattern -----
37class NotificationFactory {
38public:
39    static shared_ptr&lt;Notification&gt; createNotification(const string&amp; type) {
40        if (type == "email") return make_shared&lt;EmailNotification&gt;();
41        else if (type == "sms") return make_shared&lt;SMSNotification&gt;();
42        else if (type == "popup") return make_shared&lt;PopupNotification&gt;();
43        else throw runtime_error("❌ Unknown notification type: " + type);
44    }
45};
46
47// ----- Notification Manager -----
48class NotificationManager {
49    vector&lt;shared_ptr&lt;Notification&gt;&gt; notifications;
50public:
51    void addChannel(shared_ptr&lt;Notification&gt; n) {
52        notifications.push_back(move(n));
53    }
54
55    void notifyAll(const string&amp; message) {
56        cout &lt;&lt; "\n🔔 Sending notifications...\n";
57        for (auto&amp; n : notifications)
58            n-&gt;send(message);
59    }
60};
61
62// ----- Main Program -----
63int main() {
64    NotificationManager manager;
65
66    manager.addChannel(NotificationFactory::createNotification("email"));
67    manager.addChannel(NotificationFactory::createNotification("sms"));
68    manager.addChannel(NotificationFactory::createNotification("popup"));
69
70    manager.notifyAll("Order #1234 has been shipped!");
71
72    // Add new notification dynamically (plug &amp; play)
