music-player.cpp<pre><code>  1// music_player_design.cpp
  2// Simplified C++ implementation of the mermaid design for a modular music player
  3// Demonstrates core components: SongManager, PlaylistManager, PlaybackStrategy variants,
  4// PlayerController, DeviceManager and OutputDevice implementations.
  5
  6#include &lt;algorithm&gt;
  7#include &lt;chrono&gt;
  8#include &lt;functional&gt;
  9#include &lt;iostream&gt;
 10#include &lt;memory&gt;
 11#include &lt;mutex&gt;
 12#include &lt;random&gt;
 13#include &lt;string&gt;
 14#include &lt;thread&gt;
 15#include &lt;unordered_map&gt;
 16#include &lt;vector&gt;
 17
 18namespace MusicPlayer {
 19
 20struct Song {
 21    int id;
 22    std::string title;
 23    int durationSeconds; // fake metadata
 24};
 25
 26// Simple SongManager: stores songs and returns metadata
 27class SongManager {
 28public:
 29    void addSong(const Song &amp;s) { songs_[s.id] = s; }
 30    Song getSong(int id) const {
 31        auto it = songs_.find(id);
 32        if (it != songs_.end()) return it-&gt;second;
 33        throw std::runtime_error("Song not found");
 34    }
 35private:
 36    std::unordered_map&lt;int, Song&gt; songs_;
 37};
 38
 39// Playlist container
 40class Playlist {
 41public:
 42    explicit Playlist(std::string name) : name_(std::move(name)) {}
 43    void addSongId(int songId) { songIds_.push_back(songId); }
 44    void removeSongId(int songId) {
 45        songIds_.erase(std::remove(songIds_.begin(), songIds_.end(), songId), songIds_.end());
 46    }
 47    const std::vector&lt;int&gt;&amp; getSongIds() const { return songIds_; }
 48    const std::string&amp; getName() const { return name_; }
 49private:
 50    std::string name_;
 51    std::vector&lt;int&gt; songIds_;
 52};
 53
 54class PlaylistManager {
 55public:
 56    std::shared_ptr&lt;Playlist&gt; createPlaylist(const std::string &amp;name) {
 57        auto p = std::make_shared&lt;Playlist&gt;(name);
 58        playlists_.push_back(p);
 59        return p;
 60    }
 61    void deletePlaylist(const std::string &amp;name) {
 62        playlists_.erase(std::remove_if(playlists_.begin(), playlists_.end(), [&amp;](const auto &amp;p){ return p-&gt;getName()==name; }), playlists_.end());
 63    }
 64private:
 65    std::vector&lt;std::shared_ptr&lt;Playlist&gt;&gt; playlists_;
 66};
 67
 68// Output devices
 69class OutputDevice {
 70public:
 71    virtual ~OutputDevice() = default;
 72    virtual void playAudio(const Song &amp;song) = 0; // simplified: "play" prints
 73    virtual void stopAudio() = 0;
 74    virtual std::string getName() const = 0;
 75};
 76
 77class BluetoothSpeaker : public OutputDevice {
 78public:
 79    void playAudio(const Song &amp;song) override {
 80        std::cout &lt;&lt; "[BluetoothSpeaker] Playing: " &lt;&lt; song.title &lt;&lt; " (" &lt;&lt; song.durationSeconds &lt;&lt; "s)\n";
 81    }
 82    void stopAudio() override { std::cout &lt;&lt; "[BluetoothSpeaker] Stopped\n"; }
 83    std::string getName() const override { return "Bluetooth Speaker"; }
 84};
 85
 86class WiredSpeaker : public OutputDevice {
 87public:
 88    void playAudio(const Song &amp;song) override {
 89        std::cout &lt;&lt; "[WiredSpeaker] Playing: " &lt;&lt; song.title &lt;&lt; " (" &lt;&lt; song.durationSeconds &lt;&lt; "s)\n";
 90    }
 91    void stopAudio() override { std::cout &lt;&lt; "[WiredSpeaker] Stopped\n"; }
 92    std::string getName() const override { return "Wired Speaker"; }
 93};
 94
 95class Headphones : public OutputDevice {
 96public:
 97    void playAudio(const Song &amp;song) override {
 98        std::cout &lt;&lt; "[Headphones] Playing (private): " &lt;&lt; song.title &lt;&lt; " (" &lt;&lt; song.durationSeconds &lt;&lt; "s)\n";
 99    }
100    void stopAudio() override { std::cout &lt;&lt; "[Headphones] Stopped\n"; }
101    std::string getName() const override { return "Headphones"; }
102};
103
104// DeviceManager: manages devices and active device
105class DeviceManager {
106public:
107    void addDevice(std::shared_ptr&lt;OutputDevice&gt; d) { devices_.push_back(std::move(d)); }
108    void listDevices() const {
109        std::cout &lt;&lt; "Available devices:\n";
110        for (size_t i = 0; i &lt; devices_.size(); ++i) {
111            std::cout &lt;&lt; "  [" &lt;&lt; i &lt;&lt; "] " &lt;&lt; devices_[i]-&gt;getName() &lt;&lt; "\n";
112        }
113    }
114    bool switchTo(size_t index) {
115        if (index &gt;= devices_.size()) return false;
116        activeDevice_ = devices_[index];
117        std::cout &lt;&lt; "Switched to device: " &lt;&lt; activeDevice_-&gt;getName() &lt;&lt; "\n";
118        return true;
119    }
120    std::shared_ptr&lt;OutputDevice&gt; getActiveDevice() const { return activeDevice_; }
121private:
122    std::vector&lt;std::shared_ptr&lt;OutputDevice&gt;&gt; devices_;
123    std::shared_ptr&lt;OutputDevice&gt; activeDevice_;
124};
125
126// PlaybackStrategy interface
127class PlaybackStrategy {
128public:
129    virtual ~PlaybackStrategy() = default;
130    virtual void setPlaylist(const std::vector&lt;int&gt; &amp;songIds) = 0;
131    virtual int getCurrentSongId() = 0; // returns -1 if none
132    virtual int next() = 0; // advances and returns new song id or -1
133    virtual int previous() = 0;
134    virtual void reset() = 0;
135};
136
137// Sequential playback
138class SequentialPlayback : public PlaybackStrategy {
139public:
140    void setPlaylist(const std::vector&lt;int&gt; &amp;songIds) override { order_ = songIds; pos_ = 0; }
141    int getCurrentSongId() override { if (order_.empty() || pos_ &gt;= order_.size()) return -1; return order_[pos_]; }
142    int next() override { if (pos_ + 1 &lt; order_.size()) ++pos_; else pos_ = order_.size(); return getCurrentSongId(); }
143    int previous() override { if (pos_ == 0) return getCurrentSongId(); --pos_; return getCurrentSongId(); }
144    void reset() override { pos_ = 0; }
145private:
146    std::vector&lt;int&gt; order_;
147    size_t pos_ = 0;
148};
149
150// Shuffle playback
151class ShufflePlayback : public PlaybackStrategy {
152public:
153    void setPlaylist(const std::vector&lt;int&gt; &amp;songIds) override {
154        original_ = songIds;
155        order_ = songIds;
156        std::random_device rd;
157        std::mt19937 g(rd());
158        std::shuffle(order_.begin(), order_.end(), g);
159        pos_ = 0;
160    }
161    int getCurrentSongId() override { if (order_.empty() || pos_ &gt;= order_.size()) return -1; return order_[pos_]; }
162    int next() override { if (pos_ + 1 &lt; order_.size()) ++pos_; else pos_ = order_.size(); return getCurrentSongId(); }
163    int previous() override { if (pos_ == 0) return getCurrentSongId(); --pos_; return getCurrentSongId(); }
164    void reset() override { pos_ = 0; std::random_device rd; std::mt19937 g(rd()); std::shuffle(order_.begin(), order_.end(), g); }
165private:
166    std::vector&lt;int&gt; original_;
167    std::vector&lt;int&gt; order_;
168    size_t pos_ = 0;
169};
170
171// Custom playback: user provides a custom order
172class CustomPlayback : public PlaybackStrategy {
173public:
174    explicit CustomPlayback(const std::vector&lt;int&gt; &amp;customOrder = {}) { order_ = customOrder; pos_ = 0; }
175    void setPlaylist(const std::vector&lt;int&gt; &amp;songIds) override {
176        // If custom order was given and matches the songIds set, keep it. Otherwise default to given list.
177        // For simplicity we default to songIds if custom order is empty.
178        if (order_.empty()) { order_ = songIds; }
179        pos_ = 0;
180    }
181    int getCurrentSongId() override { if (order_.empty() || pos_ &gt;= order_.size()) return -1; return order_[pos_]; }
182    int next() override { if (pos_ + 1 &lt; order_.size()) ++pos_; else pos_ = order_.size(); return getCurrentSongId(); }
183    int previous() override { if (pos_ == 0) return getCurrentSongId(); --pos_; return getCurrentSongId(); }
184    void reset() override { pos_ = 0; }
185private:
186    std::vector&lt;int&gt; order_;
187    size_t pos_ = 0;
188};
189
190// PlayerController: orchestrates playback
191class PlayerController {
192public:
193    PlayerController(const SongManager &amp;sm, DeviceManager &amp;dm) : songManager_(sm), deviceManager_(dm) {}
194
195    void setPlaybackStrategy(std::unique_ptr&lt;PlaybackStrategy&gt; strat) {
196        std::lock_guard&lt;std::mutex&gt; lock(m_);
197        playbackStrategy_ = std::move(strat);
198        if (currentPlaylist_) playbackStrategy_-&gt;setPlaylist(currentPlaylist_-&gt;getSongIds());
199    }
200
201    void loadPlaylist(std::shared_ptr&lt;Playlist&gt; p) {
202        std::lock_guard&lt;std::mutex&gt; lock(m_);
203        currentPlaylist_ = std::move(p);
204        if (playbackStrategy_) playbackStrategy_-&gt;setPlaylist(currentPlaylist_-&gt;getSongIds());
205    }
206
207    void play() {
208        std::lock_guard&lt;std::mutex&gt; lock(m_);
209        if (!playbackStrategy_) { std::cerr &lt;&lt; "No playback strategy set\n"; return; }
210        if (!deviceManager_.getActiveDevice()) { std::cerr &lt;&lt; "No active device selected\n"; return; }
211        int id = playbackStrategy_-&gt;getCurrentSongId();
212        if (id == -1) { std::cerr &lt;&lt; "Nothing to play\n"; return; }
213        Song s = songManager_.getSong(id);
214        playing_ = true;
215        std::cout &lt;&lt; "-&gt; Playing: " &lt;&lt; s.title &lt;&lt; "\n";
216        deviceManager_.getActiveDevice()-&gt;playAudio(s);
217        // NOTE: we do not actually stream audio; we simulate playing via thread sleep for demonstration
218        playThread_ = std::thread([this, s]() {
219            std::this_thread::sleep_for(std::chrono::milliseconds(std::min(2000, s.durationSeconds*1000)));
220            // when finished, auto-advance
221            std::lock_guard&lt;std::mutex&gt; lock(m_);
222            if (playing_) {
223                int nextId = playbackStrategy_-&gt;next();
224                if (nextId != -1) {
225                    // play next automatically
226                    Song nextSong = songManager_.getSong(nextId);
227                    std::cout &lt;&lt; "-&gt; Auto-advancing to: " &lt;&lt; nextSong.title &lt;&lt; "\n";
228                    deviceManager_.getActiveDevice()-&gt;playAudio(nextSong);
229                } else {
230                    std::cout &lt;&lt; "Playlist finished\n";
231                    deviceManager_.getActiveDevice()-&gt;stopAudio();
232                    playing_ = false;
233                }
234            }
235        });
236        if (playThread_.joinable()) playThread_.detach();
237    }
238
239    void pause() {
240        std::lock_guard&lt;std::mutex&gt; lock(m_);
241        if (!playing_) { std::cerr &lt;&lt; "Nothing is playing\n"; return; }
242        playing_ = false;
243        if (deviceManager_.getActiveDevice()) deviceManager_.getActiveDevice()-&gt;stopAudio();
244        std::cout &lt;&lt; "Paused\n";
245    }
246
247    void next() {
248        std::lock_guard&lt;std::mutex&gt; lock(m_);
249        if (!playbackStrategy_) return;
250        int id = playbackStrategy_-&gt;next();
251        if (id == -1) { std::cout &lt;&lt; "End of playlist\n"; return; }
252        Song s = songManager_.getSong(id);
253        if (deviceManager_.getActiveDevice()) deviceManager_.getActiveDevice()-&gt;playAudio(s);
254        playing_ = true;
255    }
256
257    void previous() {
258        std::lock_guard&lt;std::mutex&gt; lock(m_);
259        if (!playbackStrategy_) return;
260        int id = playbackStrategy_-&gt;previous();
261        if (id == -1) return;
262        Song s = songManager_.getSong(id);
263        if (deviceManager_.getActiveDevice()) deviceManager_.getActiveDevice()-&gt;playAudio(s);
264        playing_ = true;
265    }
266
267private:
268    const SongManager &amp;songManager_;
269    DeviceManager &amp;deviceManager_;
270    std::unique_ptr&lt;PlaybackStrategy&gt; playbackStrategy_;
271    std::shared_ptr&lt;Playlist&gt; currentPlaylist_;
272    bool playing_ = false;
273    std::thread playThread_;
274    std::mutex m_;
275};
276
277// Simple text-based UI demo
278class UserInterface {
279public:
280    UserInterface(PlayerController &amp;pc, DeviceManager &amp;dm, PlaylistManager &amp;pm) : player_(pc), deviceManager_(dm), playlistManager_(pm) {}
281    void showDevices() { deviceManager_.listDevices(); }
282    void showPlaylists() { std::cout &lt;&lt; "(Playlists listing is available via PlaylistManager)\n"; }
283private:
284    PlayerController &amp;player_;
285    DeviceManager &amp;deviceManager_;
286    PlaylistManager &amp;playlistManager_;
287};
288
289} // namespace MusicPlayer
290
291// ------------------------
292// Example usage (main)
293// ------------------------
294int main() {
295    using namespace MusicPlayer;
296
297    SongManager songManager;
298    songManager.addSong({1, "Song A", 3});
299    songManager.addSong({2, "Song B", 4});
300    songManager.addSong({3, "Song C", 5});
301
302    PlaylistManager playlistManager;
303    auto pl = playlistManager.createPlaylist("My Favs");
304    pl-&gt;addSongId(1);
305    pl-&gt;addSongId(2);
306    pl-&gt;addSongId(3);
307
308    DeviceManager deviceManager;
309    deviceManager.addDevice(std::make_shared&lt;BluetoothSpeaker&gt;());
310    deviceManager.addDevice(std::make_shared&lt;WiredSpeaker&gt;());
311    deviceManager.addDevice(std::make_shared&lt;Headphones&gt;());
312    deviceManager.listDevices();
313    deviceManager.switchTo(0); // bluetooth
314
315    PlayerController controller(songManager, deviceManager);
316
317    // Sequential playback
318    controller.setPlaybackStrategy(std::make_unique&lt;SequentialPlayback&gt;());
319    controller.loadPlaylist(pl);
320
321    controller.play();
322    std::this_thread::sleep_for(std::chrono::seconds(1));
323    controller.pause();
324
325    // switch device while paused
326    deviceManager.switchTo(2); // headphones
327
328    controller.play();
329
330    // demonstrate shuffle
331    controller.setPlaybackStrategy(std::make_unique&lt;ShufflePlayback&gt;());
332    controller.loadPlaylist(pl);
333    controller.play();
334
335    // keep main alive briefly to let threads print
336    std::this_thread::sleep_for(std::chrono::seconds(3));
337    return 0;
338}
</code></pre>

