proxy-design.cpp<pre><code> 1#include &lt;iostream&gt;
 2#include &lt;memory&gt;
 3#include &lt;string&gt;
 4using namespace std;
 5
 6class Subject {
 7public:
 8    virtual void request() const = 0;
 9    virtual ~Subject() = default;
10};
11
12class RealSubject : public Subject {
13public:
14    void request() const override {
15        cout &lt;&lt; "RealSubject: Handling request.\n";
16    }
17};
18
19class Proxy : public Subject {
20private:
21    mutable unique_ptr&lt;RealSubject&gt; realSubject;
22
23public:
24    void request() const override {
25        if (!realSubject) {
26            cout &lt;&lt; "Proxy: Creating RealSubject on demand (lazy initialization).\n";
27            realSubject = make_unique&lt;RealSubject&gt;();
28        }
29        cout &lt;&lt; "Proxy: Logging access before delegating.\n";
30        realSubject-&gt;request();
31    }
32};
33
34int main() {
35    cout &lt;&lt; "--- Using Proxy ---\n";
36    Proxy proxy;
37    proxy.request();   // First access - creates real subject
38    proxy.request();   // Second access - reuses existing subject
39}
