 3
 4// Abstract Handler
 5class Handler {
 6protected:
 7    Handler* successor = nullptr;
 8public:
 9    virtual ~Handler() = default;
10
11    void setSuccessor(Handler* next) {
12        successor = next;
13    }
14
15    virtual void handleRequest(int level) {
16        if (successor)
17            successor-&gt;handleRequest(level);
18    }
19};
20
21// Concrete Handler 1
