 75/* -------------------- Retry Strategy Pattern -------------------- */
 76
 77class RetryStrategy {
 78public:
 79    virtual bool shouldRetry(int attempt, const PaymentResponse &amp;lastResponse) = 0;
 80    virtual std::chrono::milliseconds nextDelay(int attempt) = 0;
 81    virtual ~RetryStrategy() = default;
 82};
 83
 84class ImmediateRetryStrategy : public RetryStrategy {
 85    int maxAttempts;
 86public:
 87    ImmediateRetryStrategy(int maxAttempts_ = 3) : maxAttempts(maxAttempts_) {}
 88    bool shouldRetry(int attempt, const PaymentResponse &amp;lastResponse) override {
 89        return attempt &lt; maxAttempts;
 90    }
 91    chrono::milliseconds nextDelay(int attempt) override {
 92        return 0ms;
 93    }
 94};
 95
 96class ScheduledRetryStrategy : public RetryStrategy {
 97    int maxAttempts;
 98    chrono::milliseconds interval;
 99public:
100    ScheduledRetryStrategy(int maxAttempts_, chrono::milliseconds interval_) : maxAttempts(maxAttempts_), interval(interval_) {}
101    bool shouldRetry(int attempt, const PaymentResponse &amp;lastResponse) override {
102        return attempt &lt; maxAttempts;
103    }
104    chrono::milliseconds nextDelay(int attempt) override {
105        return interval;
106    }
107};
108
109class ExponentialBackoffStrategy : public RetryStrategy {
110    int maxAttempts;
111    chrono::milliseconds baseDelay;
112public:
113    ExponentialBackoffStrategy(int maxAttempts_, chrono::milliseconds baseDelay_ = 200ms) : maxAttempts(maxAttempts_), baseDelay(baseDelay_) {}
114    bool shouldRetry(int attempt, const PaymentResponse &amp;lastResponse) override {
115        return attempt &lt; maxAttempts;
116    }
117    chrono::milliseconds nextDelay(int attempt) override {
118        // attempt starts from 1
119        long long factor = 1LL &lt;&lt; (attempt - 1);
120        auto ms = baseDelay.count() * factor;
121        if (ms &gt; 5000) ms = 5000; // cap 5s for demo
122        return chrono::milliseconds(ms);
123    }
124};
125
126/* -------------------- Notification Service (stub) -------------------- */
127
128class NotificationService {
129public:
130    void notifySuccess(const PaymentRequest &amp;req, const PaymentResponse &amp;resp) {
131        cout &lt;&lt; "[Notification] Payment " &lt;&lt; req.id &lt;&lt; " succeeded via " &lt;&lt; req.provider
132             &lt;&lt; " tx=" &lt;&lt; resp.providerTxId &lt;&lt; " amount=" &lt;&lt; req.amount &lt;&lt; " " &lt;&lt; req.currency &lt;&lt; "\n";
133    }
134    void notifyFailure(const PaymentRequest &amp;req, const PaymentResponse &amp;resp) {
135        cout &lt;&lt; "[Notification] Payment " &lt;&lt; req.id &lt;&lt; " FAILED via " &lt;&lt; req.provider
136             &lt;&lt; " reason=\"" &lt;&lt; resp.message &lt;&lt; "\"\n";
137    }
138};
