140/* -------------------- Payment Service (Standardized flow) -------------------- */
141
142class PaymentService {
143    unordered_map&lt;string, shared_ptr&lt;PaymentGateway&gt;&gt; providers;
144    shared_ptr&lt;RetryStrategy&gt; retryStrategy;
145    NotificationService notifier;
146
147public:
148    PaymentService(shared_ptr&lt;RetryStrategy&gt; strat) : retryStrategy(strat) {}
149
150    void registerProvider(shared_ptr&lt;PaymentGateway&gt; gateway) {
151        providers[gateway-&gt;name()] = gateway;
152    }
153
154    // Basic validation
155    bool validate(const PaymentRequest &amp;req, string &amp;err) {
156        if (req.amount &lt;= 0) { err = "amount must be &gt; 0"; return false; }
157        if (req.currency.empty()) { err = "currency missing"; return false; }
158        if (req.userId.empty()) { err = "userId missing"; return false; }
159        if (providers.find(req.provider) == providers.end()) { err = "unknown provider"; return false; }
160        return true;
161    }
162
163    PaymentResponse executeWithRetries(const PaymentRequest &amp;req) {
164        auto gw = providers.at(req.provider);
165        int attempt = 0;
166        PaymentResponse lastResp{PaymentStatus::PENDING, "", "not attempted"};
167        while (true) {
168            attempt++;
169            cout &lt;&lt; "[PaymentService] Attempt " &lt;&lt; attempt &lt;&lt; " for payment " &lt;&lt; req.id &lt;&lt; " via " &lt;&lt; gw-&gt;name() &lt;&lt; "\n";
170
171            // Call provider
172            lastResp = gw-&gt;process(req);
173
174            if (lastResp.status == PaymentStatus::SUCCESS) {
175                cout &lt;&lt; "[PaymentService] Provider returned SUCCESS on attempt " &lt;&lt; attempt &lt;&lt; "\n";
176                return lastResp;
177            }
178
179            // Provider returned FAILED - consult retry strategy
180            if (!retryStrategy-&gt;shouldRetry(attempt, lastResp)) {
181                cout &lt;&lt; "[PaymentService] Retry exhausted after attempt " &lt;&lt; attempt &lt;&lt; ". Last error: " &lt;&lt; lastResp.message &lt;&lt; "\n";
182                return lastResp;
183            }
184
185            auto delay = retryStrategy-&gt;nextDelay(attempt);
186            cout &lt;&lt; "[PaymentService] Will retry after " &lt;&lt; delay.count() &lt;&lt; "ms\n";
187            if (delay.count() &gt; 0) std::this_thread::sleep_for(delay);
188        }
189    }
190
191    // High-level standardized flow for a single payment request
192    void processPayment(PaymentRequest req) {
193        cout &lt;&lt; "=== Processing Payment id=" &lt;&lt; req.id &lt;&lt; " provider=" &lt;&lt; req.provider &lt;&lt; " amount=" &lt;&lt; req.amount &lt;&lt; " ===\n";
194        string err;
195        if (!validate(req, err)) {
196            cout &lt;&lt; "[PaymentService] Validation failed: " &lt;&lt; err &lt;&lt; "\n";
197            PaymentResponse resp{PaymentStatus::FAILED, "", "validation error: " + err};
198            notifier.notifyFailure(req, resp);
199            return;
200        }
201
202        PaymentResponse resp = executeWithRetries(req);
203
204        if (resp.status == PaymentStatus::SUCCESS) {
205            // Update status (DB write would happen here)
206            notifier.notifySuccess(req, resp);
207        } else {
208            notifier.notifyFailure(req, resp);
209        }
210    }
211};
212
213/* -------------------- Recurring Scheduler (simple demo) -------------------- */
214
215class RecurringScheduler {
216    PaymentService &amp;svc;
217    std::atomic&lt;bool&gt; running { true };
218    vector&lt;PaymentRequest&gt; scheduled; // shallow - copies are fine for demo
219    mutex mtx;
220    thread worker;
221
222public:
223    RecurringScheduler(PaymentService &amp;svc_) : svc(svc_) {}
224
225    ~RecurringScheduler() {
226        stop();
227    }
228
229    void start() {
230        worker = thread([this]() { this-&gt;runLoop(); });
231    }
232
233    void stop() {
234        running = false;
235        if (worker.joinable()) worker.join();
236    }
237
238    // schedule a recurring payment: cycles = number of times including initial
239    void scheduleRecurring(const PaymentRequest &amp;req) {
240        lock_guard&lt;mutex&gt; lk(mtx);
241        scheduled.push_back(req);
242    }
243
244private:
245    void runLoop() {
246        // For demo: we'll run limited cycles and short intervals.
247        while (running) {
248            vector&lt;PaymentRequest&gt; toRun;
249            {
250                lock_guard&lt;mutex&gt; lk(mtx);
251                toRun = scheduled; // copy
252                // remove those with 0 cycles after picking them; update remainingCycles
253                scheduled.clear();
254                for (auto &amp;r : toRun) {
255                    if (r.remainingCycles &gt; 0) r.remainingCycles--;
256                }
257            }
258
259            if (!toRun.empty()) {
260                for (auto &amp;r : toRun) {
261                    // Process one cycle
262                    svc.processPayment(r);
263
264                    // If still cycles left, re-schedule after a short demo interval
265                    if (r.remainingCycles &gt; 0) {
266                        // in real system we'd compute next execution time and persist; here we re-add to scheduled
267                        lock_guard&lt;mutex&gt; lk(mtx);
268                        scheduled.push_back(r);
269                    } else {
270                        cout &lt;&lt; "[Scheduler] Completed recurring cycles for payment " &lt;&lt; r.id &lt;&lt; "\n";
271                    }
272                }
273            }
274
275            // Demo interval between scheduler ticks
276            std::this_thread::sleep_for(700ms);
277
278            // Stopping condition for demo: when no scheduled tasks remain
279            {
280                lock_guard&lt;mutex&gt; lk(mtx);
281                if (scheduled.empty()) {
282                    // Wait one more tick and then stop automatically (for demo)
283                    std::this_thread::sleep_for(300ms);
284                    running = false;
285                }
286            }
287        }
288        cout &lt;&lt; "[Scheduler] Exiting scheduler loop\n";
289    }
290};
291
292/* -------------------- Demo / Example usage -------------------- */
293
294int main() {
295    ios::sync_with_stdio(false);
296    cin.tie(nullptr);
297
298    // Choose retry strategy for PaymentService
299    // You can swap between ImmediateRetryStrategy, ScheduledRetryStrategy, ExponentialBackoffStrategy
300    auto retryStrategy = make_shared&lt;ExponentialBackoffStrategy&gt;(4, 150ms);
301    PaymentService paymentService(retryStrategy);
302
303    // Register providers
304    paymentService.registerProvider(make_shared&lt;PaytmAdapter&gt;());
305    paymentService.registerProvider(make_shared&lt;RazorpayAdapter&gt;());
306
307    // Non-recurring example
308    PaymentRequest p1;
309    p1.id = "txn-1001";
310    p1.provider = "paytm";
311    p1.amount = 199.99;
312    p1.currency = "INR";
313    p1.userId = "user-42";
314    p1.recurring = false;
315
316    paymentService.processPayment(p1);
317
318    // Recurring example: 3 cycles total (initial + 2 more)
319    PaymentRequest rp;
320    rp.id = "rec-2001";
321    rp.provider = "razorpay";
322    rp.amount = 49.99;
323    rp.currency = "INR";
324    rp.userId = "user-77";
325    rp.recurring = true;
326    rp.remainingCycles = 3; // number of times to attempt for demo
327
328    RecurringScheduler scheduler(paymentService);
329    scheduler.scheduleRecurring(rp);
330    scheduler.start();
331
332    // Wait for scheduler to finish its demo run
333    while (true) {
334        // Keep main alive until scheduler exits (demo)
335        std::this_thread::sleep_for(200ms);
336        break;
337    }
338
339    // join and cleanup
340    scheduler.stop();
341
342    cout &lt;&lt; "=== Demo finished ===\n";
343    return 0;
344}
