 20//----------------------------------------------
 21// PRODUCT CLASS
 22//----------------------------------------------
 23class Product {
 24public:
 25    string id;
 26    string name;
 27    double price;
 28    int quantity;
 29
 30    Product(string id, string name, double price, int quantity)
 31        : id(id), name(name), price(price), quantity(quantity) {}
 32};
 33
 34//----------------------------------------------
 35// CART CLASS
 36//----------------------------------------------
 37class Cart {
 38    vector&lt;Product&gt; products;
 39    double totalAmount = 0.0;
 40
 41public:
 42    void addProduct(const Product&amp; p) {
 43        products.push_back(p);
 44        totalAmount += p.price * p.quantity;
 45    }
 46
 47    double getTotal() const { return totalAmount; }
 48
 49    void applyDiscount(double discount) {
 50        totalAmount -= discount;
 51        if (totalAmount &lt; 0) totalAmount = 0;
 52    }
 53
 54    const vector&lt;Product&gt;&amp; getProducts() const { return products; }
 55};
 56
 57//----------------------------------------------
 58// COUPON INTERFACE (Base Class)
 59//----------------------------------------------
 60class Coupon {
 61protected:
 62    string id;
 63    CouponType type;
 64    bool stackable;
 65
 66public:
 67    Coupon(string id, CouponType type, bool stackable)
 68        : id(id), type(type), stackable(stackable) {}
 69
 70    virtual ~Coupon() = default;
 71
 72    string getId() const { return id; }
 73    CouponType getType() const { return type; }
 74    bool isStackable() const { return stackable; }
 75
 76    virtual bool isApplicable(const Cart&amp; cart) const = 0;
 77    virtual void applyDiscount(Cart&amp; cart) const = 0;
 78};
