 80//----------------------------------------------
 81// FLAT DISCOUNT COUPON
 82//----------------------------------------------
 83class FlatDiscountCoupon : public Coupon {
 84    double flatAmount;
 85
 86public:
 87    FlatDiscountCoupon(string id, CouponType type, bool stackable, double amount)
 88        : Coupon(id, type, stackable), flatAmount(amount) {}
 89
 90    bool isApplicable(const Cart&amp; cart) const override {
 91        return cart.getTotal() &gt; flatAmount;
 92    }
 93
 94    void applyDiscount(Cart&amp; cart) const override {
 95        cout &lt;&lt; "Applying Flat Discount: ₹" &lt;&lt; flatAmount &lt;&lt; endl;
 96        cart.applyDiscount(flatAmount);
 97    }
 98};
 99
100//----------------------------------------------
101// PERCENTAGE DISCOUNT COUPON
102//----------------------------------------------
103class PercentageDiscountCoupon : public Coupon {
104    double percent;
105
106public:
107    PercentageDiscountCoupon(string id, CouponType type, bool stackable, double percent)
108        : Coupon(id, type, stackable), percent(percent) {}
109
110    bool isApplicable(const Cart&amp; cart) const override {
111        return cart.getTotal() &gt; 0;
112    }
113
114    void applyDiscount(Cart&amp; cart) const override {
115        double discount = cart.getTotal() * (percent / 100.0);
116        cout &lt;&lt; "Applying " &lt;&lt; percent &lt;&lt; "% Discount: ₹" &lt;&lt; discount &lt;&lt; endl;
117        cart.applyDiscount(discount);
118    }
119};
