121//----------------------------------------------
122// THREAD-SAFE COUPON REGISTRY
123//----------------------------------------------
124class ThreadSafeCouponRegistry {
125    unordered_map&lt;string, shared_ptr&lt;Coupon&gt;&gt; registry;
126    mutable mutex mtx;
127
128public:
129    void registerCoupon(shared_ptr&lt;Coupon&gt; coupon) {
130        lock_guard&lt;mutex&gt; lock(mtx);
131        registry[coupon-&gt;getId()] = coupon;
132    }
133
134    shared_ptr&lt;Coupon&gt; getCoupon(const string&amp; id) const {
135        lock_guard&lt;mutex&gt; lock(mtx);
136        auto it = registry.find(id);
137        if (it != registry.end()) return it-&gt;second;
138        return nullptr;
139    }
140
141    void removeCoupon(const string&amp; id) {
142        lock_guard&lt;mutex&gt; lock(mtx);
143        registry.erase(id);
144    }
145};
146
147//----------------------------------------------
148// COUPON MANAGER
149//----------------------------------------------
150class CouponManager {
151    ThreadSafeCouponRegistry&amp; registry;
152
153public:
154    CouponManager(ThreadSafeCouponRegistry&amp; reg) : registry(reg) {}
155
156    void applyCoupons(Cart&amp; cart, const vector&lt;string&gt;&amp; couponIds) {
157        double before = cart.getTotal();
158        cout &lt;&lt; "\nInitial Cart Total: ₹" &lt;&lt; before &lt;&lt; endl;
159
160        shared_ptr&lt;Coupon&gt; lastApplied = nullptr;
161
162        for (const string&amp; id : couponIds) {
163            auto coupon = registry.getCoupon(id);
164            if (!coupon) {
165                cout &lt;&lt; "Coupon " &lt;&lt; id &lt;&lt; " not found.\n";
166                continue;
167            }
168
169            if (lastApplied &amp;&amp; !lastApplied-&gt;isStackable() &amp;&amp; !coupon-&gt;isStackable()) {
170                cout &lt;&lt; "Coupon " &lt;&lt; id &lt;&lt; " cannot be stacked on top of another.\n";
171                continue;
172            }
173
174            if (coupon-&gt;isApplicable(cart)) {
175                coupon-&gt;applyDiscount(cart);
176                lastApplied = coupon;
177            } else {
178                cout &lt;&lt; "Coupon " &lt;&lt; id &lt;&lt; " not applicable.\n";
179            }
180        }
181
182        cout &lt;&lt; "Final Cart Total: ₹" &lt;&lt; cart.getTotal() &lt;&lt; endl;
183    }
184};
185
186//----------------------------------------------
187// MAIN (DEMO)
188//----------------------------------------------
189int main() {
190    ThreadSafeCouponRegistry registry;
191
192    registry.registerCoupon(make_shared&lt;FlatDiscountCoupon&gt;(
193        "FLAT100", CouponType::SEASONAL, true, 100));
194    registry.registerCoupon(make_shared&lt;PercentageDiscountCoupon&gt;(
195        "BANK10", CouponType::BANKING, false, 10));
196
197    Cart cart;
198    cart.addProduct(Product("P1", "Laptop", 50000, 1));
199    cart.addProduct(Product("P2", "Mouse", 1000, 2));
200
201    CouponManager manager(registry);
202    manager.applyCoupons(cart, {"FLAT100", "BANK10"});
203
204    return 0;
205}
