bridge-pattern.cpp<pre><code> 1#include &lt;iostream&gt;
 2using namespace std;
 3
 4// Implementor Interface
 5class Implementor {
 6public:
 7    virtual void operationImpl() = 0;
 8    virtual ~Implementor() = default;
 9};
10
11// Concrete Implementors
12class ConcreteImplementorA : public Implementor {
13public:
14    void operationImpl() override {
15        cout &lt;&lt; "ConcreteImplementorA: Executing operation\n";
16    }
17};
18
19class ConcreteImplementorB : public Implementor {
20public:
21    void operationImpl() override {
22        cout &lt;&lt; "ConcreteImplementorB: Performing different operation\n";
23    }
24};
25
26// Abstraction
27class Abstraction {
28protected:
29    Implementor* implementor;
30public:
31    Abstraction(Implementor* imp) : implementor(imp) {}
32    virtual void operation() {
33        implementor-&gt;operationImpl();
34    }
35    virtual ~Abstraction() = default;
36};
37
38// Refined Abstraction
39class RefinedAbstraction : public Abstraction {
40public:
41    RefinedAbstraction(Implementor* imp) : Abstraction(imp) {}
42    void operation() override {
43        cout &lt;&lt; "RefinedAbstraction: Delegating to Implementor -&gt; ";
44        implementor-&gt;operationImpl();
45    }
46};
47
48// Client Code
49int main() {
50    ConcreteImplementorA implA;
51    ConcreteImplementorB implB;
52
53    RefinedAbstraction abstractionA(&amp;implA);
54    RefinedAbstraction abstractionB(&amp;implB);
55
56    abstractionA.operation();
57    abstractionB.operation();
58
59    return 0;
60}
