 67// Warehouse (dark store) with stock
 68struct Warehouse {
 69    string id;
 70    string location; // friendly name
 71    unordered_map&lt;string,int&gt; stock; // sku -&gt; qty
 72    Warehouse(const string&amp; i, const string&amp; loc): id(i), location(loc) {}
 73};
