 75class InventoryService {
 76public:
 77    InventoryService() {}
 78
 79    // add warehouse
 80    void addWarehouse(shared_ptr&lt;Warehouse&gt; wh) {
 81        std::lock_guard&lt;std::mutex&gt; lk(mu_);
 82        warehouses_.push_back(wh);
 83        // fill cache for quick lookup (sum of stocks)
 84        rebuildCache();
 85    }
 86
 87    // check availability: returns vector of warehouses that can fulfill quantity
 88    vector&lt;shared_ptr&lt;Warehouse&gt;&gt; findWarehouses(const string&amp; sku, int qty) {
 89        std::lock_guard&lt;std::mutex&gt; lk(mu_);
 90        vector&lt;shared_ptr&lt;Warehouse&gt;&gt; res;
 91        for (auto &amp;wh : warehouses_) {
 92            auto it = wh-&gt;stock.find(sku);
 93            if (it != wh-&gt;stock.end() &amp;&amp; it-&gt;second &gt;= qty)
 94                res.push_back(wh);
 95        }
 96        return res;
 97    }
 98
 99    // allocate sku from specific warehouse (reduce stock)
100    bool allocateFromWarehouse(shared_ptr&lt;Warehouse&gt; wh, const string&amp; sku, int qty) {
101        std::lock_guard&lt;std::mutex&gt; lk(mu_);
102        auto it = wh-&gt;stock.find(sku);
103        if (it == wh-&gt;stock.end() || it-&gt;second &lt; qty) return false;
104        it-&gt;second -= qty;
105        // update cache
106        cache_[sku] -= qty;
107        if (cache_[sku] &lt; 0) cache_[sku] = 0;
108        cout &lt;&lt; "[" &lt;&lt; now() &lt;&lt; "] [InventoryService] Allocated " &lt;&lt; qty &lt;&lt; " of " &lt;&lt; sku
109             &lt;&lt; " from Warehouse[" &lt;&lt; wh-&gt;id &lt;&lt; "]\n";
110        return true;
111    }
112
113    // quick availability check via 'cache' (simulated Redis)
114    int availableCache(const string&amp; sku) {
115        std::lock_guard&lt;std::mutex&gt; lk(mu_);
116        return cache_.count(sku)? cache_[sku] : 0;
117    }
118
119    void rebuildCache() {
120        cache_.clear();
121        for (auto &amp;wh : warehouses_) {
122            for (auto &amp;p : wh-&gt;stock) cache_[p.first] += p.second;
123        }
124    }
125
126    vector&lt;shared_ptr&lt;Warehouse&gt;&gt; getAllWarehouses() {
127        std::lock_guard&lt;std::mutex&gt; lk(mu_);
128        return warehouses_;
129    }
130
131private:
132    vector&lt;shared_ptr&lt;Warehouse&gt;&gt; warehouses_;
133    unordered_map&lt;string,int&gt; cache_;
134    std::mutex mu_;
135};
