iterator-design.cpp<pre><code> 1#include &lt;iostream&gt;
 2#include &lt;vector&gt;
 3#include &lt;memory&gt;
 4using namespace std;
 5
 6// Iterator Interface
 7class Iterator {
 8public:
 9    virtual bool hasNext() = 0;
10    virtual int next() = 0;
11    virtual ~Iterator() = default;
12};
13
14// Concrete Iterator
15class NumberIterator : public Iterator {
16    vector&lt;int&gt; numbers;
17    size_t index = 0;
18
19public:
20    NumberIterator(const vector&lt;int&gt;&amp; nums) : numbers(nums) {}
21    bool hasNext() override {
22        return index &lt; numbers.size();
23    }
24    int next() override {
25        return hasNext() ? numbers[index++] : -1;
26    }
27};
28
29// Aggregate (Collection) Interface
30class Aggregate {
31public:
32    virtual unique_ptr&lt;Iterator&gt; createIterator() = 0;
33    virtual ~Aggregate() = default;
34};
35
36// Concrete Collection
37class NumberCollection : public Aggregate {
38    vector&lt;int&gt; numbers;
39
40public:
41    void addNumber(int n) { numbers.push_back(n); }
42    unique_ptr&lt;Iterator&gt; createIterator() override {
43        return make_unique&lt;NumberIterator&gt;(numbers);
44    }
45};
46
47// Client Code
48int main() {
49    NumberCollection collection;
50    collection.addNumber(10);
51    collection.addNumber(20);
52    collection.addNumber(30);
53
54    auto iterator = collection.createIterator();
55    while (iterator-&gt;hasNext()) {
56        cout &lt;&lt; iterator-&gt;next() &lt;&lt; " ";
57    }
58    cout &lt;&lt; endl;
59    return 0;
60}
</code></pre>

