 3#include &lt;memory&gt;
 4#include &lt;string&gt;
 5using namespace std;
 6
 7// Flyweight Interface
 8class Shape {
 9public:
10    virtual void draw(int x, int y) = 0;
11    virtual ~Shape() = default;
12};
13
14// Concrete Flyweight
15class Circle : public Shape {
16    string color; // Intrinsic (shared) state
17public:
18    Circle(const string&amp; c) : color(c) {}
19    void draw(int x, int y) override {
20        cout &lt;&lt; "Drawing " &lt;&lt; color &lt;&lt; " circle at (" &lt;&lt; x &lt;&lt; ", " &lt;&lt; y &lt;&lt; ")\n";
21    }
22};
23
24// Flyweight Factory
25class ShapeFactory {
26    unordered_map&lt;string, shared_ptr&lt;Shape&gt;&gt; circleMap;
27public:
28    shared_ptr&lt;Shape&gt; getCircle(const string&amp; color) {
29        auto it = circleMap.find(color);
30        if (it != circleMap.end()) {
31            return it-&gt;second; // Reuse existing
32        }
33        cout &lt;&lt; "Creating new circle of color: " &lt;&lt; color &lt;&lt; endl;
34        auto circle = make_shared&lt;Circle&gt;(color);
35        circleMap[color] = circle;
36        return circle;
37    }
38};
39
40// Client Code
41int main() {
42    ShapeFactory factory;
43
44    auto redCircle = factory.getCircle("Red");
45    redCircle-&gt;draw(10, 20);
46
47    auto blueCircle = factory.getCircle("Blue");
48    blueCircle-&gt;draw(30, 40);
49
50    auto anotherRed = factory.getCircle("Red"); // Shared
51    anotherRed-&gt;draw(50, 60);
52
53    return 0;
54}
