splitwise-clone.cpp<pre><code>  1/*
  2  splitwise_clone.cpp
  3  A compact, single-file C++ skeleton for a Splitwise-like system.
  4  - In-memory "DB" using unordered_maps
  5  - Classes: User, Group, Expense, ExpenseService, SettlementService
  6  - Demonstrates adding users, creating groups, adding expenses,
  7    computing balances and simplifying debts (greedy algorithm).
  8
  9  Compile: g++ -std=c++17 splitwise_clone.cpp -O2 -o splitwise
 10  Run:     ./splitwise
 11*/
 12
 13#include &lt;bits/stdc++.h&gt;
 14using namespace std;
 15using ID = long long;
 16
 17static ID NEXT_ID = 1;
 18inline ID gen_id() { return NEXT_ID++; }
 19
 20struct User {
 21    ID id;
 22    string name;
 23    string email;
 24    explicit User(string name_, string email_): id(gen_id()), name(std::move(name_)), email(std::move(email_)) {}
 25};
 26
 27struct ExpenseItem {
 28    ID paid_by;          // user id who paid
 29    double amount = 0.0; // amount for this item
 30    vector&lt;pair&lt;ID,double&gt;&gt; splits; // pair&lt;user_id, share&gt;
 31};
 32
 33struct Expense {
 34    ID id;
 35    ID group_id;
 36    string title;
 37    double total = 0.0;
 38    ID created_by;
 39    time_t created_at;
 40    vector&lt;ExpenseItem&gt; items; // for extensibility; typically one item per expense
 41
 42    Expense(ID group, string title_, double total_, ID creator): id(gen_id()), group_id(group), title(std::move(title_)), total(total_), created_by(creator), created_at(time(nullptr)) {}
 43};
 44
 45struct Group {
 46    ID id;
 47    string name;
 48    unordered_set&lt;ID&gt; members; // user ids
 49    explicit Group(string name_): id(gen_id()), name(std::move(name_)) {}
 50};
 51
 52// Simple in-memory "DB"
 53struct InMemoryDB {
 54    unordered_map&lt;ID, shared_ptr&lt;User&gt;&gt; users;
 55    unordered_map&lt;ID, shared_ptr&lt;Group&gt;&gt; groups;
 56    unordered_map&lt;ID, shared_ptr&lt;Expense&gt;&gt; expenses;
 57
 58    ID create_user(const string &amp;name, const string &amp;email) {
 59        auto u = make_shared&lt;User&gt;(name, email);
 60        users[u-&gt;id] = u;
 61        return u-&gt;id;
 62    }
 63    ID create_group(const string &amp;name, const vector&lt;ID&gt; &amp;member_ids) {
 64        auto g = make_shared&lt;Group&gt;(name);
 65        for (auto m: member_ids) g-&gt;members.insert(m);
 66        groups[g-&gt;id] = g;
 67        return g-&gt;id;
 68    }
 69    ID create_expense(ID group_id, const string &amp;title, double total, ID creator) {
 70        auto e = make_shared&lt;Expense&gt;(group_id, title, total, creator);
 71        expenses[e-&gt;id] = e;
 72        return e-&gt;id;
 73    }
 74};
 75
 76// ExpenseService: handles creating expenses and calculating per-user net balances
 77struct ExpenseService {
 78    InMemoryDB &amp;db;
 79    ExpenseService(InMemoryDB &amp;db_): db(db_) {}
 80
 81    // Adds a simple expense that is split equally among the given participants
 82    ID add_equal_split_expense(ID group_id, const string &amp;title, double total, ID paid_by, const vector&lt;ID&gt; &amp;participants) {
 83        if (db.groups.find(group_id) == db.groups.end()) throw runtime_error("group not found");
 84        if (db.users.find(paid_by) == db.users.end()) throw runtime_error("payer not found");
 85
 86        ID exp_id = db.create_expense(group_id, title, total, paid_by);
 87        auto &amp;e = db.expenses[exp_id];
 88        ExpenseItem item;
 89        item.paid_by = paid_by;
 90        item.amount = total;
 91        double share = total / (double)participants.size();
 92        for (auto uid: participants) item.splits.push_back({uid, share});
 93        e-&gt;items.push_back(item);
 94        e-&gt;total = total;
 95        return exp_id;
 96    }
 97
 98    // Adds an expense with custom splits (user-&gt;share)
 99    ID add_custom_split_expense(ID group_id, const string &amp;title, double total, ID paid_by, const vector&lt;pair&lt;ID,double&gt;&gt; &amp;splits) {
100        if (db.groups.find(group_id) == db.groups.end()) throw runtime_error("group not found");
101        if (db.users.find(paid_by) == db.users.end()) throw runtime_error("payer not found");
102        double sum = 0.0; for (auto &amp;p: splits) sum += p.second;
103        // Allow small FP epsilon
104        if (fabs(sum - total) &gt; 1e-6) throw runtime_error("splits do not sum to total");
105
106        ID exp_id = db.create_expense(group_id, title, total, paid_by);
107        auto &amp;e = db.expenses[exp_id];
108        ExpenseItem item;
109        item.paid_by = paid_by;
110        item.amount = total;
111        item.splits = splits;
112        e-&gt;items.push_back(item);
113        e-&gt;total = total;
114        return exp_id;
115    }
116
117    // Calculate net balances for a specific group: positive means others owe this user, negative means user owes others
118    unordered_map&lt;ID, double&gt; compute_group_balances(ID group_id) {
119        if (db.groups.find(group_id) == db.groups.end()) throw runtime_error("group not found");
120        unordered_map&lt;ID, double&gt; net; // user -&gt; net
121        auto &amp;group = db.groups[group_id];
122
123        // initialize net balances
124        for (auto uid: group-&gt;members) net[uid] = 0.0;
125
126        for (auto &amp;kv: db.expenses) {
127            auto &amp;e = kv.second;
128            if (e-&gt;group_id != group_id) continue;
129            for (auto &amp;item: e-&gt;items) {
130                ID payer = item.paid_by;
131                double amount = item.amount;
132                // payer paid amount; others owe their share
133                net[payer] += amount; // temporarily +total
134                for (auto &amp;s: item.splits) {
135                    ID uid = s.first;
136                    double share = s.second;
137                    net[uid] -= share; // they owe share
138                }
139            }
140        }
141        // After this: net[u] &gt; 0 means u is creditor (should receive), &lt;0 means debtor
142        return net;
143    }
144};
145
146// Simplify debts: greedily match largest creditor with largest debtor until all settled
147struct SettlementService {
148    // Returns list of transactions to settle debts: tuple&lt;from (debtor), to (creditor), amount&gt;
149    static vector&lt;tuple&lt;ID,ID,double&gt;&gt; simplify_and_settle(const unordered_map&lt;ID,double&gt; &amp;net_map, double eps = 1e-6) {
150        // Build two heaps: creditors (positive), debtors (negative)
151        priority_queue&lt;pair&lt;double, ID&gt;&gt; creditors; // (amount, id)
152        priority_queue&lt;pair&lt;double, ID&gt;&gt; debtors;   // (abs(amount), id)
153
154        for (auto &amp;kv: net_map) {
155            ID uid = kv.first;
156            double val = kv.second;
157            if (val &gt; eps) creditors.push({val, uid});
158            else if (val &lt; -eps) debtors.push({-val, uid});
159        }
160
161        vector&lt;tuple&lt;ID,ID,double&gt;&gt; settlements;
162        while (!creditors.empty() &amp;&amp; !debtors.empty()) {
163            auto c = creditors.top(); creditors.pop();
164            auto d = debtors.top(); debtors.pop();
165            double amount = min(c.first, d.first);
166            ID creditor_id = c.second;
167            ID debtor_id = d.second;
168            settlements.emplace_back(debtor_id, creditor_id, amount);
169
170            double c_rem = c.first - amount;
171            double d_rem = d.first - amount;
172            if (c_rem &gt; eps) creditors.push({c_rem, creditor_id});
173            if (d_rem &gt; eps) debtors.push({d_rem, debtor_id});
174        }
175        return settlements;
176    }
177};
178
179// Utility: pretty print balances and settlements
180void print_balances(const unordered_map&lt;ID,double&gt; &amp;net, const InMemoryDB &amp;db) {
181    cout &lt;&lt; "-- Net Balances --\n";
182    for (auto &amp;kv: net) {
183        ID uid = kv.first; double v = kv.second;
184        string name = (db.users.count(uid) ? db.users.at(uid)-&gt;name : to_string(uid));
185        cout &lt;&lt; name &lt;&lt; " (" &lt;&lt; uid &lt;&lt; ") : " &lt;&lt; fixed &lt;&lt; setprecision(2) &lt;&lt; v &lt;&lt; "\n";
186    }
187}
188
189void print_settlements(const vector&lt;tuple&lt;ID,ID,double&gt;&gt; &amp;settlements, const InMemoryDB &amp;db) {
190    cout &lt;&lt; "-- Settlements Suggested --\n";
191    for (auto &amp;t: settlements) {
192        ID from, to; double amt; tie(from,to,amt) = t;
193        string fromName = (db.users.count(from) ? db.users.at(from)-&gt;name : to_string(from));
194        string toName   = (db.users.count(to)   ? db.users.at(to)-&gt;name   : to_string(to));
195        cout &lt;&lt; fromName &lt;&lt; " -&gt; " &lt;&lt; toName &lt;&lt; " : " &lt;&lt; fixed &lt;&lt; setprecision(2) &lt;&lt; amt &lt;&lt; "\n";
196    }
197}
198
199// Demo usage in main
200int main() {
201    ios::sync_with_stdio(false);
202    cin.tie(nullptr);
203
204    InMemoryDB db;
205    ExpenseService expenseService(db);
206
207    // Create users
208    ID alice = db.create_user("Alice", "alice@example.com");
209    ID bob   = db.create_user("Bob",   "bob@example.com");
210    ID carol = db.create_user("Carol", "carol@example.com");
211    ID dave  = db.create_user("Dave",  "dave@example.com");
212
213    // Create a group with Alice, Bob, Carol, Dave
214    vector&lt;ID&gt; members = {alice, bob, carol, dave};
215    ID trip = db.create_group("Weekend Trip", members);
216
217    // Alice pays 120 split equally among 4
218    expenseService.add_equal_split_expense(trip, "Groceries", 120.0, alice, members);
219
220    // Bob pays 60 split between Alice and Bob only
221    expenseService.add_custom_split_expense(trip, "Taxi", 60.0, bob, {{alice,30.0},{bob,30.0}});
222
223    // Carol pays 80 split among Bob, Carol, Dave (3 people)
224    expenseService.add_custom_split_expense(trip, "Dinner", 80.0, carol, {{bob, 26.6666667},{carol,26.6666667},{dave,26.6666666}});
225
226    // Compute balances
227    auto net = expenseService.compute_group_balances(trip);
228    print_balances(net, db);
229
230    // Simplify debts and print suggested settlements
231    auto settlements = SettlementService::simplify_and_settle(net);
232    print_settlements(settlements, db);
233
234    return 0;
235}
