state-design.cpp<pre><code> 1#include &lt;iostream&gt;
 2#include &lt;memory&gt;
 3using namespace std;
 4
 5// Forward declaration
 6class State;
 7class VendingMachine;
 8
 9// --- State Interface ---
10class State {
11public:
12    virtual void insertCoin(VendingMachine* vm) = 0;
13    virtual void selectItem(VendingMachine* vm) = 0;
14    virtual void dispense(VendingMachine* vm) = 0;
15    virtual ~State() = default;
16};
17
18// --- Context Class ---
19class VendingMachine {
20    shared_ptr&lt;State&gt; state;
21public:
22    void setState(shared_ptr&lt;State&gt; newState) { state = newState; }
23    void insertCoin() { state-&gt;insertCoin(this); }
24    void selectItem() { state-&gt;selectItem(this); }
25    void dispense() { state-&gt;dispense(this); }
26
27    // Getters for reusable static states
28    static shared_ptr&lt;State&gt; noCoinState;
29    static shared_ptr&lt;State&gt; hasCoinState;
30    static shared_ptr&lt;State&gt; dispenseState;
31};
32
33// --- Concrete States ---
34class NoCoinState : public State {
35public:
36    void insertCoin(VendingMachine* vm) override {
37        cout &lt;&lt; "Coin inserted.\n";
38        vm-&gt;setState(VendingMachine::hasCoinState);
39    }
40    void selectItem(VendingMachine* vm) override {
41        cout &lt;&lt; "Insert coin first.\n";
42    }
43    void dispense(VendingMachine* vm) override {
44        cout &lt;&lt; "Cannot dispense. No coin.\n";
45    }
46};
47
48class HasCoinState : public State {
49public:
50    void insertCoin(VendingMachine* vm) override {
51        cout &lt;&lt; "Coin already inserted.\n";
52    }
53    void selectItem(VendingMachine* vm) override {
54        cout &lt;&lt; "Item selected.\n";
55        vm-&gt;setState(VendingMachine::dispenseState);
56    }
57    void dispense(VendingMachine* vm) override {
58        cout &lt;&lt; "Select item first.\n";
59    }
60};
61
62class DispenseState : public State {
63public:
64    void insertCoin(VendingMachine* vm) override {
65        cout &lt;&lt; "Please wait, dispensing item.\n";
66    }
67    void selectItem(VendingMachine* vm) override {
68        cout &lt;&lt; "Already dispensing.\n";
69    }
70    void dispense(VendingMachine* vm) override {
71        cout &lt;&lt; "Item dispensed!\n";
72        vm-&gt;setState(VendingMachine::noCoinState);
73    }
74};
75
76// --- Define static states ---
