 57class RulesEngine {
 58public:
 59    bool checkWin(const vector&lt;vector&lt;char&gt;&gt;&amp; board, char player) {
 60        int n = board.size();
 61
 62        // Check rows &amp; columns
 63        for (int i = 0; i &lt; n; ++i) {
 64            bool rowWin = true, colWin = true;
 65            for (int j = 0; j &lt; n; ++j) {
 66                if (board[i][j] != player) rowWin = false;
 67                if (board[j][i] != player) colWin = false;
 68            }
 69            if (rowWin || colWin) return true;
 70        }
 71
 72        // Check diagonals
 73        bool diag1 = true, diag2 = true;
 74        for (int i = 0; i &lt; n; ++i) {
 75            if (board[i][i] != player) diag1 = false;
 76            if (board[i][n - i - 1] != player) diag2 = false;
 77        }
 78        return diag1 || diag2;
 79    }
 80
 81    bool isDraw(const vector&lt;vector&lt;char&gt;&gt;&amp; board) {
 82        for (auto&amp; row : board)
 83            for (auto&amp; cell : row)
 84                if (cell == '\0') return false;
 85        return true;
 86    }
 87};
