chess.cpp<pre><code>  1// multiplayer_chess_sim.cpp
  2// Single-file simulation of a multiplayer chess system with matchmaking, chat, simple game engine, and session management.
  3// Compile: g++ -std=c++17 -pthread multiplayer_chess_sim.cpp -o chess_sim
  4// Run: ./chess_sim
  5
  6#include &lt;bits/stdc++.h&gt;
  7#include &lt;thread&gt;
  8#include &lt;mutex&gt;
  9#include &lt;condition_variable&gt;
 10#include &lt;atomic&gt;
 11using namespace std;
 12
 13/* -----------------------------
 14   Utility / Types
 15   ----------------------------- */
 16using UserId = int;
 17using MatchId = int;
 18enum class Piece { EMPTY, P, R, N, B, Q, K };
 19enum class Color { NONE, WHITE, BLACK };
 20
 21struct PlayerInfo {
 22    UserId id;
 23    string name;
 24    int score; // rating
 25};
 26
 27/* -----------------------------
 28   Thread-safe Queue for Chat
 29   ----------------------------- */
 30template&lt;typename T&gt;
 31class TSQueue {
 32    mutex m;
 33    condition_variable cv;
 34    deque&lt;T&gt; q;
 35public:
 36    void push(const T&amp; v) {
 37        {
 38            lock_guard&lt;mutex&gt; lk(m);
 39            q.push_back(v);
 40        }
 41        cv.notify_one();
 42    }
 43    // wait and pop one
 44    T wait_pop() {
 45        unique_lock&lt;mutex&gt; lk(m);
 46        cv.wait(lk, [&amp;]{ return !q.empty(); });
 47        T v = q.front(); q.pop_front();
 48        return v;
 49    }
 50    bool try_pop(T &amp;out) {
 51        lock_guard&lt;mutex&gt; lk(m);
 52        if(q.empty()) return false;
 53        out = q.front(); q.pop_front();
 54        return true;
 55    }
 56    bool empty() {
 57        lock_guard&lt;mutex&gt; lk(m);
 58        return q.empty();
 59    }
 60};
 61
 62/* -----------------------------
 63   User Management (simple)
 64   ----------------------------- */
 65class UserManager {
