 88/* -----------------------------
 89   Simple Chess Board &amp; Engine
 90   - 8x8 board
 91   - Basic rules: piece movement checks (no castling/en-passant), simple checkmate detection is NOT complete.
 92   - For demonstration only.
 93   ----------------------------- */
 94
 95struct Cell {
 96    Piece piece;
 97    Color color;
 98    Cell(): piece(Piece::EMPTY), color(Color::NONE) {}
 99    Cell(Piece p, Color c): piece(p), color(c) {}
100    bool empty() const { return piece == Piece::EMPTY; }
101};
102
103struct Pos {
104    int r, c;
105    Pos(int rr=0,int cc=0): r(rr), c(cc) {}
106    bool inBounds() const { return r&gt;=0 &amp;&amp; r&lt;8 &amp;&amp; c&gt;=0 &amp;&amp; c&lt;8; }
107    bool operator==(const Pos&amp; o) const { return r==o.r &amp;&amp; c==o.c; }
108};
109
110string posToStr(const Pos&amp; p) {
111    string s;
112    s += char('a' + p.c);
113    s += char('1' + (7 - p.r)); // row 0 is rank 8
114    return s;
115}
116
117class GameEngine {
118    array&lt;array&lt;Cell,8&gt;,8&gt; board;
119    Color turn;
120    atomic&lt;bool&gt; finished;
121    mutex m; // protect board + state
122
123public:
124    GameEngine() { reset(); }
125
126    void reset() {
127        lock_guard&lt;mutex&gt; lk(m);
128        // Clear
129        for (int i=0;i&lt;8;i++) for (int j=0;j&lt;8;j++) board[i][j]=Cell();
130        // Setup pawns
131        for (int j=0;j&lt;8;j++) {
132            board[1][j] = Cell(Piece::P, Color::BLACK);
133            board[6][j] = Cell(Piece::P, Color::WHITE);
134        }
135        // Rooks
136        board[0][0]=board[0][7]=Cell(Piece::R, Color::BLACK);
137        board[7][0]=board[7][7]=Cell(Piece::R, Color::WHITE);
138        // Knights
139        board[0][1]=board[0][6]=Cell(Piece::N, Color::BLACK);
140        board[7][1]=board[7][6]=Cell(Piece::N, Color::WHITE);
141        // Bishops
142        board[0][2]=board[0][5]=Cell(Piece::B, Color::BLACK);
143        board[7][2]=board[7][5]=Cell(Piece::B, Color::WHITE);
144        // Queens
145        board[0][3]=Cell(Piece::Q, Color::BLACK);
146        board[7][3]=Cell(Piece::Q, Color::WHITE);
147        // Kings
148        board[0][4]=Cell(Piece::K, Color::BLACK);
149        board[7][4]=Cell(Piece::K, Color::WHITE);
150
151        turn = Color::WHITE;
152        finished = false;
153    }
154
155    // Very simple move parser from "e2e4" format
156    bool parsePos(const string &amp;s, Pos &amp;out) {
157        if (s.size() != 2) return false;
158        char file = s[0];
159        char rank = s[1];
160        if (file &lt; 'a' || file &gt; 'h') return false;
161        if (rank &lt; '1' || rank &gt; '8') return false;
162        int c = file - 'a';
163        int r = 7 - (rank - '1');
164        out = Pos(r,c);
165        return true;
166    }
167
168    // Basic move validation (not exhaustive)
169    bool validMove(const Pos &amp;from, const Pos &amp;to, Color player) {
170        if (!from.inBounds() || !to.inBounds()) return false;
171        Cell src = board[from.r][from.c];
172        Cell dst = board[to.r][to.c];
173        if (src.empty()) return false;
174        if (src.color != player) return false;
175        if (!dst.empty() &amp;&amp; dst.color == player) return false;
176
177        int dr = to.r - from.r;
178        int dc = to.c - from.c;
179        Piece p = src.piece;
180        switch (p) {
181            case Piece::P: {
182                int dir = (src.color==Color::WHITE) ? -1 : 1;
183                // single step
184                if (dc == 0 &amp;&amp; dr == dir &amp;&amp; dst.empty()) return true;
185                // initial double step
186                if (dc == 0 &amp;&amp; dr == 2*dir) {
187                    int startRow = (src.color==Color::WHITE) ? 6 : 1;
188                    if (from.r == startRow &amp;&amp; dst.empty() &amp;&amp; board[from.r+dir][from.c].empty()) return true;
189                }
190                // capture
191                if (abs(dc)==1 &amp;&amp; dr==dir &amp;&amp; !dst.empty() &amp;&amp; dst.color != src.color) return true;
192                return false;
193            }
194            case Piece::R: {
195                if (dr!=0 &amp;&amp; dc!=0) return false;
196                int rr = (dr==0) ? 0 : (dr&gt;0?1:-1);
197                int cc = (dc==0) ? 0 : (dc&gt;0?1:-1);
198                int steps = max(abs(dr),abs(dc));
199                for (int s=1;s&lt;steps;s++) if (!board[from.r + rr*s][from.c + cc*s].empty()) return false;
200                return true;
201            }
202            case Piece::B: {
203                if (abs(dr) != abs(dc)) return false;
204                int rr = (dr&gt;0?1:-1), cc=(dc&gt;0?1:-1);
205                for (int s=1;s&lt;abs(dr);s++) if (!board[from.r + rr*s][from.c + cc*s].empty()) return false;
206                return true;
207            }
208            case Piece::Q: {
209                if (dr==0 || dc==0) {
210                    int rr = (dr==0)?0:(dr&gt;0?1:-1);
211                    int cc = (dc==0)?0:(dc&gt;0?1:-1);
212                    int steps = max(abs(dr),abs(dc));
213                    for (int s=1;s&lt;steps;s++) if (!board[from.r + rr*s][from.c + cc*s].empty()) return false;
214                    return true;
215                } else if (abs(dr)==abs(dc)) {
216                    int rr=(dr&gt;0?1:-1), cc=(dc&gt;0?1:-1);
217                    for (int s=1;s&lt;abs(dr);s++) if (!board[from.r + rr*s][from.c + cc*s].empty()) return false;
218                    return true;
219                } else return false;
220            }
221            case Piece::N: {
222                if ((abs(dr)==2 &amp;&amp; abs(dc)==1) || (abs(dr)==1 &amp;&amp; abs(dc)==2)) return true;
223                return false;
224            }
225            case Piece::K: {
226                if (max(abs(dr),abs(dc))==1) return true;
227                return false;
228            }
229            default: return false;
230        }
231    }
232
233    // Move piece, return true if moved
234    bool makeMove(const string &amp;fromStr, const string &amp;toStr, Color player, string &amp;err) {
235        lock_guard&lt;mutex&gt; lk(m);
236        if (finished) { err = "Game already finished"; return false; }
237        Pos from, to;
238        if (!parsePos(fromStr, from) || !parsePos(toStr, to)) { err = "Invalid coordinates"; return false; }
239        if (!validMove(from, to, player)) { err = "Illegal move"; return false; }
240        // perform
241        Cell src = board[from.r][from.c];
242        Cell dst = board[to.r][to.c];
243        board[to.r][to.c] = src;
244        board[from.r][from.c] = Cell();
245        // pawn promotion simplification: promote to queen if reaches last rank
246        if (src.piece == Piece::P) {
247            if ((src.color==Color::WHITE &amp;&amp; to.r==0) || (src.color==Color::BLACK &amp;&amp; to.r==7)) {
248                board[to.r][to.c].piece = Piece::Q;
249            }
250        }
251        // swap turn
252        turn = (turn==Color::WHITE?Color::BLACK:Color::WHITE);
253        return true;
254    }
255
256    // Very simple "is game over?" - here we just detect if a king is captured.
257    bool checkFinished() {
258        lock_guard&lt;mutex&gt; lk(m);
259        bool whiteKing=false, blackKing=false;
260        for (int i=0;i&lt;8;i++) for (int j=0;j&lt;8;j++) {
261            if (board[i][j].piece == Piece::K) {
262                if (board[i][j].color == Color::WHITE) whiteKing=true;
263                if (board[i][j].color == Color::BLACK) blackKing=true;
264            }
265        }
266        if (!whiteKing || !blackKing) {
267            finished = true;
268            return true;
269        }
270        return false;
271    }
272
273    // Return FEN-like simple board print
274    string boardStr() {
275        lock_guard&lt;mutex&gt; lk(m);
276        string s;
277        for (int i=0;i&lt;8;i++) {
278            for (int j=0;j&lt;8;j++) {
279                Cell c = board[i][j];
280                char ch='.';
281                if (!c.empty()) {
282                    switch (c.piece) {
283                        case Piece::P: ch='p'; break;
284                        case Piece::R: ch='r'; break;
285                        case Piece::N: ch='n'; break;
286                        case Piece::B: ch='b'; break;
287                        case Piece::Q: ch='q'; break;
288                        case Piece::K: ch='k'; break;
289                        default: ch='?';
290                    }
291                    if (c.color==Color::WHITE) ch = toupper(ch);
292                }
293                s += ch; s += ' ';
294            }
295            s += '\n';
296        }
297        return s;
298    }
299
300    Color currentTurn() {
301        lock_guard&lt;mutex&gt; lk(m);
302        return turn;
303    }
304
305    void forceFinish() {
306        finished = true;
307    }
308};
