335/* -----------------------------
336   Match / Session Management
337   ----------------------------- */
338
339class Match {
340    MatchId id;
341    PlayerInfo whitePlayer;
342    PlayerInfo blackPlayer;
343    GameEngine game;
344    ChatSystem chat;
345    thread matchThread;
346    atomic&lt;bool&gt; running;
347    mutex outm; // printing
348    atomic&lt;bool&gt; whiteQuit, blackQuit;
349
350public:
351    Match(MatchId mid, PlayerInfo p1, PlayerInfo p2)
352    : id(mid), whitePlayer(p1), blackPlayer(p2), running(false), whiteQuit(false), blackQuit(false)
353    {
354        // We assign higher rated player to white arbitrarily
355        if (whitePlayer.score &lt; blackPlayer.score) swap(whitePlayer, blackPlayer);
356    }
357
358    void start() {
359        running = true;
360        matchThread = thread(&amp;Match::run, this);
361    }
362
363    void joinThread() {
364        if (matchThread.joinable()) matchThread.join();
365    }
366
367    void run() {
368        {
369            lock_guard&lt;mutex&gt; lk(outm);
370            cout &lt;&lt; "[Match " &lt;&lt; id &lt;&lt; "] Started: " &lt;&lt; whitePlayer.name &lt;&lt; " (white) vs " &lt;&lt; blackPlayer.name &lt;&lt; " (black)\n";
371        }
372        game.reset();
373        // Simulated play loop: to show functioning, we will alternate "random moves" for a few turns or accept quittings
374        // In a real system we would accept moves from networked players.
375        int turnCount = 0;
376        while (running) {
377            if (whiteQuit || blackQuit) {
378                lock_guard&lt;mutex&gt; lk(outm);
379                cout &lt;&lt; "[Match " &lt;&lt; id &lt;&lt; "] A player quit. ";
380                if (whiteQuit) cout &lt;&lt; whitePlayer.name &lt;&lt; " quit. ";
381                if (blackQuit) cout &lt;&lt; blackPlayer.name &lt;&lt; " quit. ";
382                cout &lt;&lt; "Terminating match.\n";
383                game.forceFinish();
384                break;
385            }
386            // For demo: process chat messages if any
387            ChatMessage cm;
388            while (chat.tryReceive(cm)) {
389                lock_guard&lt;mutex&gt; lk(outm);
390                string who = (cm.from == whitePlayer.id) ? whitePlayer.name : (cm.from == blackPlayer.id ? blackPlayer.name : "unknown");
391                cout &lt;&lt; "[Match " &lt;&lt; id &lt;&lt; "][CHAT] " &lt;&lt; who &lt;&lt; ": " &lt;&lt; cm.text &lt;&lt; "\n";
392            }
393
394            // Check finished
395            if (game.checkFinished()) {
396                lock_guard&lt;mutex&gt; lk(outm);
397                cout &lt;&lt; "[Match " &lt;&lt; id &lt;&lt; "] Game finished by capture of king.\n";
398                break;
399            }
400
401            // For simulation, make a dummy legal move for current player from a list of candidate moves.
402            Color cur = game.currentTurn();
403            PlayerInfo curPlayer = (cur==Color::WHITE) ? whitePlayer : blackPlayer;
404            // simulate quitting: if random small chance player quits
405            if ((rand()%1000) &lt; 5) { // tiny chance
406                if (cur==Color::WHITE) whiteQuit = true;
407                else blackQuit = true;
408                continue;
409            }
410
411            // We will search simple moves: move any pawn forward if possible, else try small moves
412            bool moved = false;
413            // trying to find any legal move brute-force
414            for (int r=0;r&lt;8 &amp;&amp; !moved;r++) for (int c=0;c&lt;8 &amp;&amp; !moved;c++) {
415                Pos from(r,c);
416                // build coordinate strings
417                // attempt to move to all possible squares
418                for (int rr=0;rr&lt;8 &amp;&amp; !moved;rr++) for (int cc=0;cc&lt;8 &amp;&amp; !moved;cc++) {
419                    Pos to(rr,cc);
420                    // skip same
421                    if (from==to) continue;
422                    // prepare strings
423                    string fromS = posToStr(from);
424                    string toS = posToStr(to);
425                    string err;
426                    if (game.makeMove(fromS, toS, cur, err)) {
427                        // succeeded
428                        lock_guard&lt;mutex&gt; lk(outm);
429                        cout &lt;&lt; "[Match " &lt;&lt; id &lt;&lt; "] " &lt;&lt; curPlayer.name &lt;&lt; " (" &lt;&lt; (cur==Color::WHITE?"W":"B") &lt;&lt; ") moved " &lt;&lt; fromS &lt;&lt; " -&gt; " &lt;&lt; toS &lt;&lt; "\n";
430                        moved = true;
431                        break;
432                    }
433                }
434            }
435            // if no move found (stalemate or blocked) — finish
436            if (!moved) {
437                lock_guard&lt;mutex&gt; lk(outm);
438                cout &lt;&lt; "[Match " &lt;&lt; id &lt;&lt; "] No legal move found for current player. Ending match.\n";
439                break;
440            }
441
442            turnCount++;
443            if (turnCount &gt; 200) { // limit for demo
444                lock_guard&lt;mutex&gt; lk(outm);
445                cout &lt;&lt; "[Match " &lt;&lt; id &lt;&lt; "] Move limit reached, ending match as draw.\n";
446                break;
447            }
448
449            // small sleep to let output be readable
450            this_thread::sleep_for(chrono::milliseconds(50));
451        } // end while
452
453        {
454            lock_guard&lt;mutex&gt; lk(outm);
455            cout &lt;&lt; "[Match " &lt;&lt; id &lt;&lt; "] cleaning up\n";
456        }
457        running = false;
458    }
459
460    void sendChat(UserId from, const string &amp;text) {
461        chat.send(from, text);
462    }
463
464    void playerQuit(UserId uid) {
465        if (uid == whitePlayer.id) whiteQuit = true;
466        else if (uid == blackPlayer.id) blackQuit = true;
467    }
468
469    bool isRunning() const { return running; }
470};
