472/* -----------------------------
473   MatchMaker
474   - Simple queue-based matchmaking by score proximity
475   ----------------------------- */
476
477class MatchMaker {
478    mutex m;
479    MatchId nextMatchId = 1;
480    vector&lt;shared_ptr&lt;Match&gt;&gt; matches;
481    deque&lt;PlayerInfo&gt; waiting;
482    int scoreTolerance = 200; // +- rating window
483public:
484    // add waiting player and attempt to create matches
485    void enterQueue(const PlayerInfo &amp;p) {
486        lock_guard&lt;mutex&gt; lk(m);
487        waiting.push_back(p);
488        tryMakeMatches();
489    }
490
491    // naive matching: scan waiting for pairs within tolerance
492    void tryMakeMatches() {
493        for (size_t i=0;i&lt;waiting.size();++i) {
494            for (size_t j=i+1;j&lt;waiting.size();++j) {
495                if (abs(waiting[i].score - waiting[j].score) &lt;= scoreTolerance) {
496                    // create match
497                    PlayerInfo a = waiting[i];
498                    PlayerInfo b = waiting[j];
499                    // remove j then i
500                    waiting.erase(waiting.begin()+j);
501                    waiting.erase(waiting.begin()+i);
502                    makeMatch(a,b);
503                    // restart scanning
504                    tryMakeMatches();
505                    return;
506                }
507            }
508        }
509    }
510
511    void makeMatch(const PlayerInfo &amp;a, const PlayerInfo &amp;b) {
512        MatchId mid = nextMatchId++;
513        auto match = make_shared&lt;Match&gt;(mid, a, b);
514        matches.push_back(match);
515        match-&gt;start();
516    }
517
518    vector&lt;shared_ptr&lt;Match&gt;&gt; currentMatches() {
519        lock_guard&lt;mutex&gt; lk(m);
520        return matches;
521    }
522
523    // for demo: cleanup finished matches
524    void cleanup() {
525        lock_guard&lt;mutex&gt; lk(m);
526        vector&lt;shared_ptr&lt;Match&gt;&gt; active;
527        for (auto &amp;mm : matches) {
528            if (mm-&gt;isRunning()) active.push_back(mm);
529            else mm-&gt;joinThread();
530        }
531        matches.swap(active);
532    }
533};
534
535/* -----------------------------
536   Demo / Main simulation
537   ----------------------------- */
538
539int main() {
540    srand((unsigned)chrono::system_clock::now().time_since_epoch().count());
541    UserManager um;
542    MatchMaker mm;
543
544    // Create sample users
545    UserId u1 = um.registerUser("Alice", 1500);
546    UserId u2 = um.registerUser("Bob", 1520);
547    UserId u3 = um.registerUser("Charlie", 1300);
548    UserId u4 = um.registerUser("Diana", 1490);
549    UserId u5 = um.registerUser("Eve", 1700);
550
551    // Enter matchmaking queue
552    mm.enterQueue(um.getUser(u1));
553    mm.enterQueue(um.getUser(u2));
554    mm.enterQueue(um.getUser(u3));
555    mm.enterQueue(um.getUser(u4));
556    mm.enterQueue(um.getUser(u5));
557
558    // Let matches begin
559    this_thread::sleep_for(chrono::milliseconds(200));
560
561    // Find match threads and send some chat messages / quit events
562    auto active = mm.currentMatches();
563    if (!active.empty()) {
564        auto m0 = active[0];
565        // send a chat from white (we don't know exact assignment here but demo call)
566        m0-&gt;sendChat(u1, "gl hf!");
567        m0-&gt;sendChat(u2, "good luck!");
568        // simulate a quit after some time
569        this_thread::sleep_for(chrono::milliseconds(300));
570        // For demonstration, make one player quit
571        m0-&gt;playerQuit(u2);
572    }
573
574    // Let system run a bit
575    for (int i=0;i&lt;50;i++) {
576        mm.cleanup();
577        this_thread::sleep_for(chrono::milliseconds(200));
578    }
579
580    // Wait for remaining matches to finish
581    auto all = mm.currentMatches();
582    for (auto &amp;m : all) {
583        m-&gt;joinThread();
584    }
585
586    cout &lt;&lt; "Simulation done.\n";
587    return 0;
588}
