46// Concrete Visitor 1
47class ConcreteVisitor1 : public Visitor {
48public:
49    void visit(ConcreteElementA* element) override {
50        cout &lt;&lt; "ConcreteVisitor1: Visiting ElementA → ";
51        element-&gt;operationA();
52    }
