59// Concrete Visitor 2
60class ConcreteVisitor2 : public Visitor {
61public:
62    void visit(ConcreteElementA* element) override {
63        cout &lt;&lt; "ConcreteVisitor2: Performing analysis on ElementA.\n";
64    }
