70// Object Structure
71class ObjectStructure {
72    vector&lt;shared_ptr&lt;Element&gt;&gt; elements;
73public:
74    void addElement(shared_ptr&lt;Element&gt; e) { elements.push_back(e); }
75
76    void accept(Visitor* visitor) {
